        legend: { show: false },
        scales: {
          x: { time: false },
          penalty: {},
        },
      })
    : null;
  uPlotInst?.setData([xs, ys]);

  const usePlot = (node: HTMLDivElement) => {
    if (uPlotInst) {
      node.appendChild(uPlotInst.root);
    }
  };
</script>

<div class="root" use:usePlot />

<style lang="css">
  :global(html, body) {
    overflow: hidden;
  }

  .root {
    overflow: hidden;
  }
</style>
